mapper.update:
update `wms_ware_sku`
set stock=stock + #{skuNum}
where sku_id = #{skuId}
  and ware_id = #{wareId}

update wms_ware_sku set stock_locked = stock_locked + #{num}
                    where sku_id = #{skuId}
                      and ware_id = #{wareId}
                      and stock - stock_locked >= #{num}

mapper.select:
SELECT sku_id skuId, sum(stock - stock_locked) hasStock FROM `wms_ware_sku` where sku_id in
#{id}
GROUP BY sku_id
